package oop0308;

public class test01_print {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("자바");
		System.out.println("파이썬");
		System.out.println(123);
		System.out.println("123");
		// " 및 ' 및 \를 단순기호로 인식하고싶을떄 :
		//역슬래시
		System.out.println("\'");
		System.out.println("\'");
		System.out.println("\\");
	}

}
